import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  Zap, 
  BarChart3, 
  BookOpen, 
  Play, 
  TrendingUp, 
  Leaf, 
  Gauge,
  Award,
  Users,
  Target
} from 'lucide-react';
import './LandingPage.css';

const LandingPage = () => {
  const features = [
    {
      icon: Zap,
      title: 'AI-Powered Optimization',
      description: 'Advanced reinforcement learning algorithms optimize driving patterns for maximum efficiency.',
      color: '#00ff88'
    },
    {
      icon: Leaf,
      title: 'Sustainability Focus',
      description: 'Reduce CO₂ emissions and fuel consumption through intelligent driving strategies.',
      color: '#00ff88'
    },
    {
      icon: Gauge,
      title: 'Real-time Analytics',
      description: 'Live telemetry data and performance metrics during simulation runs.',
      color: '#ff6b35'
    },
    {
      icon: Award,
      title: 'Performance Comparison',
      description: 'Compare AI vs Human driving performance across multiple metrics.',
      color: '#ff1744'
    }
  ];

  const stats = [
    { number: '25%', label: 'Fuel Savings', icon: TrendingUp },
    { number: '30%', label: 'CO₂ Reduction', icon: Leaf },
    { number: '15%', label: 'Faster Lap Times', icon: Gauge },
    { number: '1000+', label: 'Simulations Run', icon: Users }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="landing-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <motion.div 
            className="hero-content"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.div className="hero-badge" variants={itemVariants}>
              <Zap size={16} />
              <span>AI-Powered Driving Optimization</span>
            </motion.div>
            
            <motion.h1 className="hero-title" variants={itemVariants}>
              Smart Mobility for a
              <span className="gradient-text"> Sustainable Future</span>
            </motion.h1>
            
            <motion.p className="hero-description" variants={itemVariants}>
              Experience the future of driving with our AI-powered simulator that optimizes 
              performance while minimizing environmental impact. Compare human vs AI driving 
              patterns and discover how reinforcement learning can revolutionize mobility.
            </motion.p>
            
            <motion.div className="hero-actions" variants={itemVariants}>
              <Link to="/simulator" className="btn-primary">
                <Play size={20} />
                Start Simulation
              </Link>
              <Link to="/insights" className="btn-outline">
                <BookOpen size={20} />
                Learn About RL
              </Link>
            </motion.div>
          </motion.div>
          
          <motion.div 
            className="hero-visual"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            <div className="dashboard-preview">
              <div className="dashboard-header">
                <div className="dashboard-dots">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
                <span className="dashboard-title">Live Simulation</span>
              </div>
              <div className="dashboard-content">
                <div className="metric-card">
                  <div className="metric-value">1:23.456</div>
                  <div className="metric-label">Lap Time</div>
                </div>
                <div className="metric-card">
                  <div className="metric-value">2.4 kWh</div>
                  <div className="metric-label">Energy Used</div>
                </div>
                <div className="metric-card">
                  <div className="metric-value">-15%</div>
                  <div className="metric-label">CO₂ Saved</div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <motion.div 
            className="stats-grid"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            {stats.map((stat, index) => {
              const Icon = stat.icon;
              return (
                <motion.div key={index} className="stat-card" variants={itemVariants}>
                  <div className="stat-icon">
                    <Icon size={32} />
                  </div>
                  <div className="stat-number">{stat.number}</div>
                  <div className="stat-label">{stat.label}</div>
                </motion.div>
              );
            })}
          </motion.div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="container">
          <motion.div 
            className="section-header"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h2 className="section-title" variants={itemVariants}>
              Why Choose EcoDrive Simulator?
            </motion.h2>
            <motion.p className="section-description" variants={itemVariants}>
              Our platform combines cutting-edge AI technology with real-world driving data 
              to create the most advanced driving optimization simulator available.
            </motion.p>
          </motion.div>
          
          <motion.div 
            className="features-grid"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            {features.map((feature, index) => {
              const Icon = feature.icon;
              return (
                <motion.div key={index} className="feature-card" variants={itemVariants}>
                  <div className="feature-icon" style={{ color: feature.color }}>
                    <Icon size={48} />
                  </div>
                  <h3 className="feature-title">{feature.title}</h3>
                  <p className="feature-description">{feature.description}</p>
                </motion.div>
              );
            })}
          </motion.div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <motion.div 
            className="cta-content"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h2 className="cta-title" variants={itemVariants}>
              Ready to Experience the Future of Driving?
            </motion.h2>
            <motion.p className="cta-description" variants={itemVariants}>
              Join thousands of users who have already discovered how AI can optimize 
              their driving performance while reducing environmental impact.
            </motion.p>
            <motion.div className="cta-actions" variants={itemVariants}>
              <Link to="/simulator" className="btn-primary">
                <Play size={20} />
                Start Your First Simulation
              </Link>
              <Link to="/insights" className="btn-outline">
                <BarChart3 size={20} />
                View AI vs Human Data
              </Link>
            </motion.div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
