import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  MapPin, 
  Car, 
  Cloud, 
  Settings, 
  Play, 
  ArrowLeft,
  Zap,
  Leaf,
  Flame,
  Droplets,
  Sun,
  CloudRain
} from 'lucide-react';
import './SimulationSetup.css';

const SimulationSetup = () => {
  const navigate = useNavigate();
  const [selectedTrack, setSelectedTrack] = useState('silverstone');
  const [drivingMode, setDrivingMode] = useState('eco');
  const [weather, setWeather] = useState('dry');
  const [surface, setSurface] = useState('smooth');
  const [modelVersion, setModelVersion] = useState('ppo');

  const tracks = [
    {
      id: 'silverstone',
      name: 'Silverstone Circuit',
      country: 'United Kingdom',
      length: '5.891 km',
      difficulty: 'Medium',
      image: '/api/placeholder/300/200'
    },
    {
      id: 'monza',
      name: 'Autodromo Nazionale Monza',
      country: 'Italy',
      length: '5.793 km',
      difficulty: 'High',
      image: '/api/placeholder/300/200'
    },
    {
      id: 'yas_marina',
      name: 'Yas Marina Circuit',
      country: 'UAE',
      length: '5.554 km',
      difficulty: 'Medium',
      image: '/api/placeholder/300/200'
    },
    {
      id: 'spa',
      name: 'Circuit de Spa-Francorchamps',
      country: 'Belgium',
      length: '7.004 km',
      difficulty: 'High',
      image: '/api/placeholder/300/200'
    },
    {
      id: 'monaco',
      name: 'Circuit de Monaco',
      country: 'Monaco',
      length: '3.337 km',
      difficulty: 'Very High',
      image: '/api/placeholder/300/200'
    }
  ];

  const drivingModes = [
    {
      id: 'eco',
      name: 'Eco Mode',
      description: 'Maximum fuel efficiency and minimal emissions',
      icon: Leaf,
      color: '#00ff88',
      characteristics: ['Low fuel consumption', 'Smooth acceleration', 'Early braking', 'Optimal gear shifting']
    },
    {
      id: 'balanced',
      name: 'Balanced Mode',
      description: 'Balance between performance and efficiency',
      icon: Zap,
      color: '#ff6b35',
      characteristics: ['Moderate fuel consumption', 'Balanced acceleration', 'Standard braking', 'Adaptive gear shifting']
    },
    {
      id: 'aggressive',
      name: 'Aggressive Mode',
      description: 'Maximum performance and speed',
      icon: Flame,
      color: '#ff1744',
      characteristics: ['High fuel consumption', 'Rapid acceleration', 'Late braking', 'Performance gear shifting']
    }
  ];

  const weatherConditions = [
    { id: 'dry', name: 'Dry', icon: Sun, description: 'Optimal conditions' },
    { id: 'light_rain', name: 'Light Rain', icon: CloudRain, description: 'Reduced grip' },
    { id: 'heavy_rain', name: 'Heavy Rain', icon: Droplets, description: 'Significant grip loss' }
  ];

  const surfaceConditions = [
    { id: 'smooth', name: 'Smooth', description: 'Optimal surface conditions' },
    { id: 'rough', name: 'Rough', description: 'Increased rolling resistance' },
    { id: 'wet', name: 'Wet', description: 'Reduced traction' }
  ];

  const modelVersions = [
    { id: 'ppo', name: 'PPO (Proximal Policy Optimization)', description: 'Stable and reliable' },
    { id: 'sac', name: 'SAC (Soft Actor-Critic)', description: 'High performance' },
    { id: 'td3', name: 'TD3 (Twin Delayed DDPG)', description: 'Advanced optimization' }
  ];

  const handleStartSimulation = () => {
    const simulationConfig = {
      track: selectedTrack,
      drivingMode,
      weather,
      surface,
      modelVersion
    };
    
    // Store config in localStorage for the dashboard
    localStorage.setItem('simulationConfig', JSON.stringify(simulationConfig));
    
    // Navigate to dashboard
    navigate('/dashboard');
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="simulation-setup">
      <div className="container">
        <motion.div 
          className="setup-header"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="header-nav" variants={itemVariants}>
            <Link to="/" className="back-button">
              <ArrowLeft size={20} />
              Back to Home
            </Link>
          </motion.div>
          
          <motion.h1 className="setup-title" variants={itemVariants}>
            Simulation Setup
          </motion.h1>
          
          <motion.p className="setup-description" variants={itemVariants}>
            Configure your simulation parameters to experience AI-powered driving optimization
          </motion.p>
        </motion.div>

        <motion.div 
          className="setup-content"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          {/* Track Selection */}
          <motion.div className="setup-section" variants={itemVariants}>
            <div className="section-header">
              <MapPin size={24} />
              <h2>Select Track</h2>
            </div>
            
            <div className="track-grid">
              {tracks.map((track) => (
                <motion.div
                  key={track.id}
                  className={`track-card ${selectedTrack === track.id ? 'selected' : ''}`}
                  onClick={() => setSelectedTrack(track.id)}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <div className="track-image">
                    <div className="track-placeholder">
                      <MapPin size={32} />
                    </div>
                  </div>
                  <div className="track-info">
                    <h3>{track.name}</h3>
                    <p className="track-country">{track.country}</p>
                    <div className="track-details">
                      <span>Length: {track.length}</span>
                      <span>Difficulty: {track.difficulty}</span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Driving Mode Selection */}
          <motion.div className="setup-section" variants={itemVariants}>
            <div className="section-header">
              <Car size={24} />
              <h2>Driving Mode</h2>
            </div>
            
            <div className="mode-grid">
              {drivingModes.map((mode) => {
                const Icon = mode.icon;
                return (
                  <motion.div
                    key={mode.id}
                    className={`mode-card ${drivingMode === mode.id ? 'selected' : ''}`}
                    onClick={() => setDrivingMode(mode.id)}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    style={{ '--mode-color': mode.color }}
                  >
                    <div className="mode-icon">
                      <Icon size={32} />
                    </div>
                    <h3>{mode.name}</h3>
                    <p>{mode.description}</p>
                    <ul className="mode-characteristics">
                      {mode.characteristics.map((char, index) => (
                        <li key={index}>{char}</li>
                      ))}
                    </ul>
                  </motion.div>
                );
              })}
            </div>
          </motion.div>

          {/* Weather & Surface Conditions */}
          <motion.div className="setup-section" variants={itemVariants}>
            <div className="section-header">
              <Cloud size={24} />
              <h2>Conditions</h2>
            </div>
            
            <div className="conditions-grid">
              <div className="condition-group">
                <h3>Weather</h3>
                <div className="condition-options">
                  {weatherConditions.map((condition) => {
                    const Icon = condition.icon;
                    return (
                      <motion.div
                        key={condition.id}
                        className={`condition-option ${weather === condition.id ? 'selected' : ''}`}
                        onClick={() => setWeather(condition.id)}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        <Icon size={24} />
                        <span>{condition.name}</span>
                        <small>{condition.description}</small>
                      </motion.div>
                    );
                  })}
                </div>
              </div>
              
              <div className="condition-group">
                <h3>Surface</h3>
                <div className="condition-options">
                  {surfaceConditions.map((condition) => (
                    <motion.div
                      key={condition.id}
                      className={`condition-option ${surface === condition.id ? 'selected' : ''}`}
                      onClick={() => setSurface(condition.id)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <span>{condition.name}</span>
                      <small>{condition.description}</small>
                    </motion.div>
                  ))}
                </div>
              </div>
            </div>
          </motion.div>

          {/* Advanced Settings */}
          <motion.div className="setup-section" variants={itemVariants}>
            <div className="section-header">
              <Settings size={24} />
              <h2>Advanced Settings</h2>
            </div>
            
            <div className="advanced-settings">
              <div className="setting-group">
                <label>AI Model Version</label>
                <select 
                  value={modelVersion} 
                  onChange={(e) => setModelVersion(e.target.value)}
                  className="form-select"
                >
                  {modelVersions.map((model) => (
                    <option key={model.id} value={model.id}>
                      {model.name} - {model.description}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </motion.div>

          {/* Start Simulation */}
          <motion.div className="setup-actions" variants={itemVariants}>
            <motion.button
              className="btn-primary start-simulation"
              onClick={handleStartSimulation}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Play size={20} />
              Start Simulation
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
};

export default SimulationSetup;
