import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  Play, 
  Pause, 
  RotateCcw, 
  ArrowLeft,
  Zap,
  Gauge,
  Leaf,
  Clock,
  MapPin,
  Car,
} from 'lucide-react';
import './SimulationDashboard.css';

const SimulationDashboard = () => {
  const navigate = useNavigate();
  const [isRunning, setIsRunning] = useState(false);
  const [currentLap, setCurrentLap] = useState(0);
  const [totalLaps, setTotalLaps] = useState(3);
  const [simulationTime, setSimulationTime] = useState(0);
  const [simulationId, setSimulationId] = useState(null);
  
  // Basic simulation data
  const [simulationData, setSimulationData] = useState({
    ai: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
    },
    human: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
    }
  });

  useEffect(() => {
    // Load simulation config
    const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
    if (!config.track) {
      navigate('/simulator');
      return;
    }
  }, [navigate]);

  const startSimulation = async () => {
    try {
      const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
      
      const response = await fetch('http://localhost:5000/api/simulation/start', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          track: config.track,
          driving_mode: config.driving_mode || 'eco',
          weather: config.weather || 'clear',
          surface: config.surface || 'dry',
          model_version: 'ppo'
        })
      });

      const result = await response.json();
      
      if (result.simulation_id) {
        setSimulationId(result.simulation_id);
        setIsRunning(true);
      } else {
        console.error('Failed to start simulation:', result.error);
      }
    } catch (error) {
      console.error('Failed to start simulation:', error);
    }
  };

  const pauseSimulation = async () => {
    if (simulationId) {
      try {
        await fetch(`http://localhost:5000/api/simulation/${simulationId}/stop`, {
          method: 'POST'
        });
      } catch (error) {
        console.error('Failed to stop simulation:', error);
      }
    }
    
    setIsRunning(false);
  };

  const resetSimulation = async () => {
    if (simulationId) {
      try {
        await fetch(`http://localhost:5000/api/simulation/${simulationId}/stop`, {
          method: 'POST'
        });
      } catch (error) {
        console.error('Failed to stop simulation:', error);
      }
    }
    
    setIsRunning(false);
    setSimulationTime(0);
    setCurrentLap(0);
    setSimulationId(null);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(1);
    return `${mins}:${secs.padStart(4, '0')}`;
  };

  return (
    <div className="simulation-dashboard">
      <div className="dashboard-container">
        {/* Header */}
        <div className="dashboard-header">
          <div className="header-left">
            <Link to="/simulator" className="back-button">
              <ArrowLeft size={20} />
              Back to Setup
            </Link>
            <div className="simulation-info">
              <h1>Live Simulation</h1>
              <div className="simulation-meta">
                <span><MapPin size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').track || 'Unknown Track'}</span>
                <span><Car size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').driving_mode || 'Eco'} Mode</span>
                <span><Clock size={16} /> {formatTime(simulationTime)}</span>
              </div>
            </div>
          </div>
          
          <div className="header-controls">
            <button 
              className={`control-btn ${isRunning ? 'pause' : 'play'}`}
              onClick={isRunning ? pauseSimulation : startSimulation}
            >
              {isRunning ? <Pause size={20} /> : <Play size={20} />}
              {isRunning ? 'Pause' : 'Start'}
            </button>
            <button className="control-btn reset" onClick={resetSimulation}>
              <RotateCcw size={20} />
              Reset
            </button>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="progress-section">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${(currentLap / totalLaps) * 100}%` }}
            ></div>
          </div>
          <div className="progress-info">
            <span>Lap {currentLap} of {totalLaps}</span>
            <span>{formatTime(simulationTime)}</span>
          </div>
        </div>

        {/* Main Content */}
        <div className="dashboard-content">
          {/* Video Panel */}
          <div className="video-panel">
            <div className="video-container">
              <h3>Live Simulation</h3>
              <div className="video-frame">
                <div className="video-placeholder">
                  <Car size={48} />
                  <p>{isRunning ? 'Simulation Running...' : 'No simulation active'}</p>
                </div>
              </div>
            </div>
          </div>

          {/* Metrics Panel */}
          <div className="metrics-panel">
            <div className="metric-card">
              <div className="metric-header">
                <Zap size={20} />
                <span>AI Performance</span>
              </div>
              <div className="metric-value">{formatTime(simulationData.ai.lapTime)}</div>
              <div className="metric-label">Lap Time</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Gauge size={20} />
                <span>Speed</span>
              </div>
              <div className="metric-value">{simulationData.ai.speed.toFixed(1)}</div>
              <div className="metric-label">km/h</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Leaf size={20} />
                <span>Energy Saved</span>
              </div>
              <div className="metric-value">{simulationData.ai.co2Saved.toFixed(1)}</div>
              <div className="metric-label">CO₂ Saved</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SimulationDashboard;
