import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import io from 'socket.io-client';
import { 
  Play, 
  Pause, 
  RotateCcw, 
  ArrowLeft,
  Zap,
  Gauge,
  Leaf,
  TrendingUp,
  Clock,
  MapPin,
  Car,
  BarChart3,
  Activity,
  Target,
  AlertCircle
} from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area } from 'recharts';
import './SimulationDashboard.css';

const SimulationDashboard = () => {
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const [isRunning, setIsRunning] = useState(false);
  const [currentLap, setCurrentLap] = useState(0);
  const [simulationTime, setSimulationTime] = useState(0);
  const [activeTab, setActiveTab] = useState('telemetry');
  const [simulationId, setSimulationId] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  
  // Real simulation data
  const [simulationData, setSimulationData] = useState({
    ai: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
      position: { x: 0, y: 0 },
      steering: 0
    },
    human: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
      position: { x: 0, y: 0 },
      steering: 0
    }
  });

  // Real telemetry data
  const [telemetryData, setTelemetryData] = useState([]);
  const [availableModels, setAvailableModels] = useState({});

  useEffect(() => {
    // Load simulation config
    const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
    if (!config.track) {
      navigate('/simulator');
      return;
    }

    // Initialize WebSocket connection
    initializeSocket();
    
    // Load available models and tracks
    loadSystemData();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [navigate]);

  const initializeSocket = () => {
    socketRef.current = io('http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      console.log('Connected to simulation server');
      setConnectionStatus('connected');
    });

    socketRef.current.on('disconnect', () => {
      console.log('Disconnected from simulation server');
      setConnectionStatus('disconnected');
    });

    socketRef.current.on('telemetry_data', (data) => {
      updateSimulationData(data);
    });

    socketRef.current.on('status', (data) => {
      console.log('Server status:', data);
    });
  };

  const loadSystemData = async () => {
    try {
      const [modelsResponse, tracksResponse] = await Promise.all([
        fetch('http://localhost:5000/api/models'),
        fetch('http://localhost:5000/api/tracks')
      ]);
      
      const models = await modelsResponse.json();
      const tracks = await tracksResponse.json();
      
      setAvailableModels(models);
      setAvailableTracks(tracks);
    } catch (error) {
      console.error('Failed to load system data:', error);
    }
  };

  const updateSimulationData = (data) => {
    if (data && data.data && data.data.length > 0) {
      const latestData = data.data[data.data.length - 1];
      
      setSimulationData(prev => ({
        ai: {
          ...prev.ai,
          lapTime: latestData.lap_time || 0,
          speed: Math.sqrt(latestData.velocity.x ** 2 + latestData.velocity.y ** 2) * 3.6, // Convert to km/h
          throttle: Math.max(0, latestData.action.speed),
          brake: Math.max(0, -latestData.action.speed),
          steering: latestData.action.steering,
          position: {
            x: latestData.position.x,
            y: latestData.position.y
          }
        },
        human: {
          ...prev.human,
          lapTime: latestData.lap_time || 0,
          speed: Math.sqrt(latestData.velocity.x ** 2 + latestData.velocity.y ** 2) * 3.6,
          throttle: Math.max(0, latestData.action.speed),
          brake: Math.max(0, -latestData.action.speed),
          steering: latestData.action.steering,
          position: {
            x: latestData.position.x,
            y: latestData.position.y
          }
        }
      }));

      // Update telemetry data
      setTelemetryData(prev => {
        const newData = [...prev, {
          time: latestData.timestamp,
          aiSpeed: Math.sqrt(latestData.velocity.x ** 2 + latestData.velocity.y ** 2) * 3.6,
          humanSpeed: Math.sqrt(latestData.velocity.x ** 2 + latestData.velocity.y ** 2) * 3.6,
          aiThrottle: Math.max(0, latestData.action.speed),
          humanThrottle: Math.max(0, latestData.action.speed),
          aiBrake: Math.max(0, -latestData.action.speed),
          humanBrake: Math.max(0, -latestData.action.speed),
          aiSteering: latestData.action.steering,
          humanSteering: latestData.action.steering,
          aiEnergy: latestData.timestamp * 0.5,
          humanEnergy: latestData.timestamp * 0.7,
          reward: latestData.reward
        }];
        
        // Keep only last 100 data points
        return newData.slice(-100);
      });

      setSimulationTime(latestData.timestamp);
    }
  };

  const startSimulation = async () => {
    try {
      const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
      
      const response = await fetch('http://localhost:5000/api/simulation/start', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          track: config.track,
          driving_mode: config.driving_mode || 'eco',
          weather: config.weather || 'clear',
          surface: config.surface || 'dry',
          model_version: config.model_version || 'ppo'
        })
      });

      const result = await response.json();
      
      if (result.simulation_id) {
        setSimulationId(result.simulation_id);
        setIsRunning(true);
        
        // Subscribe to simulation updates
        socketRef.current.emit('subscribe_simulation', {
          simulation_id: result.simulation_id
        });

        // Start polling for simulation data
        const pollInterval = setInterval(async () => {
          try {
            const dataResponse = await fetch(`http://localhost:5000/api/simulation/${result.simulation_id}/data`);
            const data = await dataResponse.json();
            updateSimulationData(data);
            
            if (data.status === 'completed' || data.status === 'error') {
              clearInterval(pollInterval);
              setIsRunning(false);
            }
          } catch (error) {
            console.error('Failed to fetch simulation data:', error);
          }
        }, 100); // Poll every 100ms

        // Store interval for cleanup
        window.simulationPollInterval = pollInterval;
      } else {
        console.error('Failed to start simulation:', result.error);
      }
    } catch (error) {
      console.error('Failed to start simulation:', error);
    }
  };

  const pauseSimulation = () => {
    setIsRunning(false);
    if (window.simulationPollInterval) {
      clearInterval(window.simulationPollInterval);
    }
  };

  const resetSimulation = async () => {
    if (simulationId) {
      try {
        await fetch(`http://localhost:5000/api/simulation/${simulationId}/stop`, {
          method: 'POST'
        });
      } catch (error) {
        console.error('Failed to stop simulation:', error);
      }
    }
    
    setIsRunning(false);
    setSimulationTime(0);
    setCurrentLap(0);
    setTelemetryData([]);
    setSimulationId(null);
    
    if (window.simulationPollInterval) {
      clearInterval(window.simulationPollInterval);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(1);
    return `${mins}:${secs.padStart(4, '0')}`;
  };

  const tabs = [
    { id: 'telemetry', label: 'Live Telemetry', icon: Activity },
    { id: 'performance', label: 'Performance', icon: TrendingUp },
    { id: 'insights', label: 'Model Insights', icon: BarChart3 }
  ];

  return (
    <div className="simulation-dashboard">
      <div className="dashboard-container">
        {/* Header */}
        <motion.div 
          className="dashboard-header"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <div className="header-left">
            <Link to="/simulator" className="back-button">
              <ArrowLeft size={20} />
              Back to Setup
            </Link>
            <div className="simulation-info">
              <h1>Live Simulation</h1>
              <div className="simulation-meta">
                <span><MapPin size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').track || 'Unknown Track'}</span>
                <span><Car size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').driving_mode || 'Eco'} Mode</span>
                <span><Clock size={16} /> {formatTime(simulationTime)}</span>
                <span className={`connection-status ${connectionStatus}`}>
                  <div className={`status-dot ${connectionStatus}`}></div>
                  {connectionStatus === 'connected' ? 'Connected' : 'Disconnected'}
                </span>
              </div>
            </div>
          </div>
          
          <div className="header-controls">
            <button 
              className={`control-btn ${isRunning ? 'pause' : 'play'}`}
              onClick={isRunning ? pauseSimulation : startSimulation}
              disabled={connectionStatus !== 'connected'}
            >
              {isRunning ? <Pause size={20} /> : <Play size={20} />}
              {isRunning ? 'Pause' : 'Start'}
            </button>
            <button className="control-btn reset" onClick={resetSimulation}>
              <RotateCcw size={20} />
              Reset
            </button>
          </div>
        </motion.div>

        {/* Connection Status Alert */}
        {connectionStatus !== 'connected' && (
          <motion.div 
            className="connection-alert"
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <AlertCircle size={16} />
            <span>Not connected to simulation server. Please ensure the backend is running.</span>
          </motion.div>
        )}

        {/* Progress Bar */}
        <motion.div 
          className="progress-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
        >
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${(currentLap / totalLaps) * 100}%` }}
            ></div>
          </div>
          <div className="progress-info">
            <span>Lap {currentLap} of {totalLaps}</span>
            <span>{formatTime(simulationTime)}</span>
          </div>
        </motion.div>

        {/* Main Content */}
        <div className="dashboard-content">
          {/* Left Panel - Metrics */}
          <motion.div 
            className="metrics-panel"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <div className="metric-card">
              <div className="metric-header">
                <Zap size={20} />
                <span>AI Performance</span>
              </div>
              <div className="metric-value">{formatTime(simulationData.ai.lapTime)}</div>
              <div className="metric-label">Lap Time</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Gauge size={20} />
                <span>Speed</span>
              </div>
              <div className="metric-value">{simulationData.ai.speed.toFixed(1)}</div>
              <div className="metric-label">km/h</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Leaf size={20} />
                <span>Energy Saved</span>
              </div>
              <div className="metric-value">{simulationData.ai.co2Saved.toFixed(1)}</div>
              <div className="metric-label">CO₂ Saved</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Target size={20} />
                <span>Steering</span>
              </div>
              <div className="metric-value">{(simulationData.ai.steering * 180 / Math.PI).toFixed(1)}</div>
              <div className="metric-label">degrees</div>
            </div>
          </motion.div>

          {/* Right Panel - Charts */}
          <motion.div 
            className="charts-panel"
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            {/* Tab Navigation */}
            <div className="tab-navigation">
              {tabs.map(tab => (
                <button
                  key={tab.id}
                  className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
                  onClick={() => setActiveTab(tab.id)}
                >
                  <tab.icon size={16} />
                  {tab.label}
                </button>
              ))}
            </div>

            {/* Tab Content */}
            <div className="tab-content">
              {activeTab === 'telemetry' && (
                <div className="chart-container">
                  <h3>Live Telemetry</h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={telemetryData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="time" />
                      <YAxis />
                      <Tooltip />
                      <Line type="monotone" dataKey="aiSpeed" stroke="#00ff88" strokeWidth={2} name="AI Speed" />
                      <Line type="monotone" dataKey="humanSpeed" stroke="#ff6b6b" strokeWidth={2} name="Human Speed" />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              )}

              {activeTab === 'performance' && (
                <div className="chart-container">
                  <h3>Performance Metrics</h3>
                  <ResponsiveContainer width="100%" height={300}>
                    <AreaChart data={telemetryData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="time" />
                      <YAxis />
                      <Tooltip />
                      <Area type="monotone" dataKey="aiEnergy" stackId="1" stroke="#00ff88" fill="#00ff88" fillOpacity={0.6} name="AI Energy" />
                      <Area type="monotone" dataKey="humanEnergy" stackId="1" stroke="#ff6b6b" fill="#ff6b6b" fillOpacity={0.6} name="Human Energy" />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>
              )}

              {activeTab === 'insights' && (
                <div className="insights-container">
                  <h3>Model Insights</h3>
                  <div className="insights-grid">
                    <div className="insight-card">
                      <h4>Available Models</h4>
                      <div className="model-list">
                        {Object.entries(availableModels).map(([key, model]) => (
                          <div key={key} className={`model-item ${model.status}`}>
                            <span className="model-name">{key.toUpperCase()}</span>
                            <span className="model-status">{model.status}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                    <div className="insight-card">
                      <h4>System Status</h4>
                      <div className="status-info">
                        <p>Connection: {connectionStatus}</p>
                        <p>Simulation ID: {simulationId || 'None'}</p>
                        <p>Data Points: {telemetryData.length}</p>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default SimulationDashboard;
