import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  BookOpen, 
  Brain, 
  Zap, 
  Leaf, 
  TrendingUp, 
  Target, 
  ArrowRight, 
  ExternalLink,
  PlayCircle,
  BarChart3,
  Lightbulb,
  Award,
  Users,
  Globe
} from 'lucide-react';
import './EducationalInsights.css';

const EducationalInsights = () => {
  const [activeTopic, setActiveTopic] = useState('rl');

  const topics = [
    {
      id: 'rl',
      title: 'What is Reinforcement Learning?',
      icon: Brain,
      color: '#00ff88',
      description: 'Learn the fundamentals of how AI learns to make decisions through trial and error.'
    },
    {
      id: 'ai-driving',
      title: 'How AI Learns to Drive Efficiently',
      icon: Zap,
      color: '#ff6b35',
      description: 'Discover how reinforcement learning algorithms optimize driving patterns for maximum efficiency.'
    },
    {
      id: 'eco-principles',
      title: 'Eco-driving Principles in Real Life',
      icon: Leaf,
      color: '#00ff88',
      description: 'Apply AI-learned techniques to your own driving for better fuel economy and reduced emissions.'
    }
  ];

  const rlContent = {
    title: 'Reinforcement Learning Fundamentals',
    sections: [
      {
        title: 'What is Reinforcement Learning?',
        content: 'Reinforcement Learning (RL) is a type of machine learning where an AI agent learns to make decisions by interacting with an environment. The agent receives rewards or penalties for its actions and learns to maximize cumulative rewards over time.',
        points: [
          'Agent: The AI system making decisions',
          'Environment: The world the agent interacts with',
          'Actions: What the agent can do',
          'Rewards: Feedback that guides learning',
          'Policy: The strategy the agent uses to choose actions'
        ]
      },
      {
        title: 'How RL Works in Driving',
        content: 'In our driving simulator, the AI agent learns to drive by:',
        points: [
          'Observing the track, speed, and vehicle state',
          'Choosing actions like acceleration, braking, and steering',
          'Receiving rewards for efficient driving and penalties for waste',
          'Gradually improving its policy through millions of simulations',
          'Optimizing for multiple objectives: speed, efficiency, and safety'
        ]
      },
      {
        title: 'Key RL Algorithms',
        content: 'Our simulator uses state-of-the-art RL algorithms:',
        points: [
          'PPO (Proximal Policy Optimization): Stable and reliable learning',
          'SAC (Soft Actor-Critic): High-performance continuous control',
          'TD3 (Twin Delayed DDPG): Advanced optimization for complex tasks'
        ]
      }
    ]
  };

  const aiDrivingContent = {
    title: 'AI Driving Optimization',
    sections: [
      {
        title: 'Learning Process',
        content: 'The AI learns to drive efficiently through a sophisticated training process:',
        points: [
          'Starts with random actions and gradually improves',
          'Learns from millions of simulated driving scenarios',
          'Optimizes for multiple objectives simultaneously',
          'Develops smooth, consistent driving patterns',
          'Adapts to different track conditions and weather'
        ]
      },
      {
        title: 'Optimization Strategies',
        content: 'The AI discovers and applies various optimization techniques:',
        points: [
          'Smooth acceleration and deceleration patterns',
          'Optimal gear selection for efficiency',
          'Predictive braking for corner entry',
          'Energy recovery through regenerative braking',
          'Adaptive speed control based on track conditions'
        ]
      },
      {
        title: 'Performance Metrics',
        content: 'The AI optimizes for several key performance indicators:',
        points: [
          'Lap time: Minimizing total time while maintaining efficiency',
          'Energy consumption: Reducing fuel/electricity usage',
          'CO₂ emissions: Minimizing environmental impact',
          'Consistency: Maintaining performance across multiple laps',
          'Smoothness: Reducing wear and improving comfort'
        ]
      }
    ]
  };

  const ecoPrinciplesContent = {
    title: 'Real-World Eco-Driving',
    sections: [
      {
        title: 'AI-Learned Techniques',
        content: 'Apply these AI-discovered techniques to your daily driving:',
        points: [
          'Smooth acceleration: Gradual speed increases save fuel',
          'Predictive braking: Anticipate stops to avoid hard braking',
          'Optimal speed maintenance: Use cruise control on highways',
          'Route optimization: Choose efficient paths with fewer stops',
          'Vehicle maintenance: Keep tires properly inflated and engine tuned'
        ]
      },
      {
        title: 'Environmental Impact',
        content: 'Small changes in driving behavior can have significant environmental benefits:',
        points: [
          'Reduce fuel consumption by 10-15% through efficient driving',
          'Lower CO₂ emissions and air pollution',
          'Decrease wear on vehicle components',
          'Improve traffic flow and reduce congestion',
          'Save money on fuel and maintenance costs'
        ]
      },
      {
        title: 'Getting Started',
        content: 'Begin implementing eco-driving techniques today:',
        points: [
          'Monitor your fuel consumption and track improvements',
          'Practice smooth acceleration and braking',
          'Plan routes to minimize stops and idling',
          'Use technology like GPS and fuel efficiency apps',
          'Join eco-driving communities for tips and support'
        ]
      }
    ]
  };

  const resources = [
    {
      title: 'International Energy Agency (IEA)',
      description: 'Global energy statistics and efficiency reports',
      url: 'https://www.iea.org',
      icon: Globe
    },
    {
      title: 'FIA Sustainability',
      description: 'Motorsport sustainability initiatives and research',
      url: 'https://www.fia.com',
      icon: Award
    },
    {
      title: 'Reinforcement Learning Research',
      description: 'Latest research papers and developments in RL',
      url: 'https://arxiv.org/list/cs.LG/recent',
      icon: BookOpen
    },
    {
      title: 'Eco-Driving Communities',
      description: 'Connect with other eco-conscious drivers',
      url: 'https://www.ecodrive.org',
      icon: Users
    }
  ];

  const getCurrentContent = () => {
    switch (activeTopic) {
      case 'rl':
        return rlContent;
      case 'ai-driving':
        return aiDrivingContent;
      case 'eco-principles':
        return ecoPrinciplesContent;
      default:
        return rlContent;
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="educational-insights">
      <div className="container">
        <motion.div 
          className="insights-header"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h1 className="insights-title" variants={itemVariants}>
            Educational Insights
          </motion.h1>
          
          <motion.p className="insights-description" variants={itemVariants}>
            Learn about reinforcement learning, AI-driven optimization, and sustainable driving practices
          </motion.p>
        </motion.div>

        {/* Topic Navigation */}
        <motion.div 
          className="topic-navigation"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="topic-tabs" variants={itemVariants}>
            {topics.map((topic) => {
              const Icon = topic.icon;
              return (
                <motion.button
                  key={topic.id}
                  className={`topic-tab ${activeTopic === topic.id ? 'active' : ''}`}
                  onClick={() => setActiveTopic(topic.id)}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  style={{ '--topic-color': topic.color }}
                >
                  <div className="tab-icon">
                    <Icon size={24} />
                  </div>
                  <div className="tab-content">
                    <h3>{topic.title}</h3>
                    <p>{topic.description}</p>
                  </div>
                  <ArrowRight size={20} className="tab-arrow" />
                </motion.button>
              );
            })}
          </motion.div>
        </motion.div>

        {/* Content Section */}
        <motion.div 
          className="content-section"
          key={activeTopic}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          <div className="content-header">
            <h2>{getCurrentContent().title}</h2>
          </div>
          
          <div className="content-sections">
            {getCurrentContent().sections.map((section, index) => (
              <motion.div
                key={index}
                className="content-card"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <h3>{section.title}</h3>
                <p>{section.content}</p>
                <ul className="content-points">
                  {section.points.map((point, pointIndex) => (
                    <li key={pointIndex}>{point}</li>
                  ))}
                </ul>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Interactive Elements */}
        <motion.div 
          className="interactive-section"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h2 className="section-title" variants={itemVariants}>
            Interactive Learning
          </motion.h2>
          
          <motion.div className="interactive-grid" variants={itemVariants}>
            <div className="interactive-card">
              <div className="card-icon">
                <PlayCircle size={32} />
              </div>
              <h4>Watch AI Learn</h4>
              <p>See how the AI agent improves its driving performance over time through reinforcement learning.</p>
              <Link to="/simulator" className="card-action">
                Start Simulation
                <ArrowRight size={16} />
              </Link>
            </div>
            
            <div className="interactive-card">
              <div className="card-icon">
                <BarChart3 size={32} />
              </div>
              <h4>Compare Performance</h4>
              <p>Analyze the differences between AI and human driving patterns in our detailed comparison tools.</p>
              <Link to="/results" className="card-action">
                View Results
                <ArrowRight size={16} />
              </Link>
            </div>
            
            <div className="interactive-card">
              <div className="card-icon">
                <Target size={32} />
              </div>
              <h4>Try Different Scenarios</h4>
              <p>Experiment with various tracks, weather conditions, and driving modes to see how AI adapts.</p>
              <Link to="/simulator" className="card-action">
                Configure Simulation
                <ArrowRight size={16} />
              </Link>
            </div>
          </motion.div>
        </motion.div>

        {/* Resources Section */}
        <motion.div 
          className="resources-section"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h2 className="section-title" variants={itemVariants}>
            Additional Resources
          </motion.h2>
          
          <motion.div className="resources-grid" variants={itemVariants}>
            {resources.map((resource, index) => {
              const Icon = resource.icon;
              return (
                <motion.a
                  key={index}
                  href={resource.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="resource-card"
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <div className="resource-icon">
                    <Icon size={24} />
                  </div>
                  <div className="resource-content">
                    <h4>{resource.title}</h4>
                    <p>{resource.description}</p>
                  </div>
                  <ExternalLink size={16} className="resource-link" />
                </motion.a>
              );
            })}
          </motion.div>
        </motion.div>

        {/* Call to Action */}
        <motion.div 
          className="cta-section"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="cta-content" variants={itemVariants}>
            <h2>Ready to Experience AI-Powered Driving?</h2>
            <p>Put your knowledge to the test and see how AI optimization can improve your driving efficiency.</p>
            <div className="cta-actions">
              <Link to="/simulator" className="btn-primary">
                <PlayCircle size={20} />
                Start Your First Simulation
              </Link>
              <Link to="/" className="btn-outline">
                <ArrowRight size={20} />
                Back to Home
              </Link>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
};

export default EducationalInsights;
