import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  Play, 
  Pause, 
  RotateCcw, 
  ArrowLeft,
  Zap,
  Gauge,
  Leaf,
  Clock,
  MapPin,
  Car,
  AlertCircle,
  Square,
} from 'lucide-react';
import './SimulationDashboard.css';

const SimulationDashboard = () => {
  const navigate = useNavigate();
  const [isRunning, setIsRunning] = useState(false);
  const [currentLap, setCurrentLap] = useState(0);
  const [totalLaps, setTotalLaps] = useState(3);
  const [simulationTime, setSimulationTime] = useState(0);
  const [simulationId, setSimulationId] = useState(null);
  const [webSimulationAvailable, setWebSimulationAvailable] = useState(false);
  const [simulationFrame, setSimulationFrame] = useState(null);
  const [simulationError, setSimulationError] = useState(null);
  const [fallbackMode, setFallbackMode] = useState(false);
  const eventSourceRef = useRef(null);
  
  // Basic simulation data
  const [simulationData, setSimulationData] = useState({
    ai: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
    },
    human: {
      lapTime: 0,
      energyConsumption: 0,
      co2Saved: 0,
      speed: 0,
      throttle: 0,
      brake: 0,
    }
  });

  useEffect(() => {
    // Load simulation config
    const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
    if (!config.track) {
      navigate('/simulator');
      return;
    }

    // Check if web simulation is available
    checkWebSimulationAvailability();
  }, [navigate]);

  const checkWebSimulationAvailability = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/web-simulation/status');
      if (response.ok) {
        const data = await response.json();
        // Web simulation is available if we get a response (even if idle)
        setWebSimulationAvailable(true);
        console.log('Web simulation status:', data);
      }
    } catch (error) {
      console.log('Web simulation not available:', error);
      setWebSimulationAvailable(false);
    }
  };

  const startSimulation = async () => {
    console.log('🚀 Starting simulation...');
    try {
      const config = JSON.parse(localStorage.getItem('simulationConfig') || '{}');
      const savedSimulationId = localStorage.getItem('simulationId');
      
      console.log('📋 Config:', config);
      console.log('🆔 Saved Simulation ID:', savedSimulationId);
      
      if (savedSimulationId) {
        // Use existing simulation ID
        console.log('♻️ Resuming existing simulation:', savedSimulationId);
        setSimulationId(savedSimulationId);
        setIsRunning(true);
        setSimulationError(null);
        
        // Start streaming if web simulation is available
        if (webSimulationAvailable) {
          console.log('📡 Starting frame streaming...');
          startFrameStreaming();
        }
      } else {
        // Start new simulation
        console.log('🆕 Starting new simulation...');
        const response = await fetch('http://localhost:5000/api/simulation/start', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            track: config.track,
            model_version: config.model_version || 'ppo',
            user: 'web_user'
          })
        });

        const result = await response.json();
        console.log('📊 Simulation start result:', result);
        
        if (result.simulation_id) {
          console.log('✅ Simulation started successfully!');
          setSimulationId(result.simulation_id);
          localStorage.setItem('simulationId', result.simulation_id);
          setIsRunning(true);
          setSimulationError(null);
          
          // If web simulation is available, start streaming frames
          if (result.web_simulation && webSimulationAvailable) {
            console.log('📡 Starting frame streaming...');
            startFrameStreaming();
          }
        } else {
          const errorMsg = result.error || 'Failed to start simulation';
          console.error('❌ Simulation start failed:', errorMsg);
          setSimulationError(errorMsg);
          
          // If F1Tenth is not available, start mock simulation
          if (errorMsg.includes('F1Tenth')) {
            console.log('🔄 Starting fallback mock simulation...');
            setFallbackMode(true);
            startMockSimulation();
          }
        }
      }
    } catch (error) {
      console.error('💥 Failed to start simulation:', error);
      setSimulationError('Failed to start simulation: ' + error.message);
      
      // Fallback to mock simulation
      console.log('🔄 Starting fallback mock simulation due to error...');
      setFallbackMode(true);
      startMockSimulation();
    }
  };

  const startMockSimulation = () => {
    console.log('Starting mock simulation in fallback mode');
    setIsRunning(true);
    setSimulationError(null);
    
    // Simulate a lap over 90 seconds
    let progress = 0;
    const interval = setInterval(() => {
      progress += 0.01; // 1% every 100ms
      const lapTime = progress * 90; // 90 second lap
      
      setSimulationTime(lapTime);
      setCurrentLap(Math.floor(progress * 10));
      
      // Update simulation data
      setSimulationData(prev => ({
        ...prev,
        ai: {
          lapTime: lapTime,
          energyConsumption: lapTime * 0.03,
          co2Saved: lapTime * 0.015,
          speed: 120 + Math.sin(progress * Math.PI) * 40,
          throttle: 60 + Math.sin(progress * Math.PI * 2) * 20,
          brake: 20 + Math.sin(progress * Math.PI * 3) * 15,
        }
      }));
      
      // Add mock frame data
      const mockFrame = {
        step: Math.floor(progress * 1000),
        time: lapTime,
        speed: 120 + Math.sin(progress * Math.PI) * 40,
        position: [progress * 100, Math.sin(progress * Math.PI) * 50],
        progress: progress
      };
      setSimulationFrame(JSON.stringify(mockFrame));
      
      if (progress >= 1.0) {
        clearInterval(interval);
        setIsRunning(false);
        
        // Store mock results
        const mockResults = {
          ai_lap_time: 87.5,
          ai_lap_time_formatted: "1:27.500",
          hamilton_lap_time: 89.2,
          hamilton_lap_time_formatted: "1:29.200",
          time_difference: -1.7,
          ai_faster: true,
          percentage_difference: -1.9
        };
        localStorage.setItem('simulationResults', JSON.stringify(mockResults));
        
        console.log('Mock simulation completed');
      }
    }, 100);
    
    // Store interval reference for cleanup
    eventSourceRef.current = { close: () => clearInterval(interval) };
  };

  const startFrameStreaming = () => {
    console.log('📡 Starting frame streaming...');
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
    }

    eventSourceRef.current = new EventSource('http://localhost:5000/api/web-simulation/stream');
    
    eventSourceRef.current.onopen = () => {
      console.log('✅ EventSource connection opened');
    };
    
    eventSourceRef.current.onerror = (error) => {
      console.error('❌ EventSource error:', error);
    };
    
    eventSourceRef.current.onmessage = (event) => {
      try {
        console.log('📨 Received streaming data:', event.data);
        const data = JSON.parse(event.data);
        
        // Update simulation data
        setSimulationData(prev => ({
          ...prev,
          ai: {
            lapTime: data.lap_time || 0,
            energyConsumption: data.energy_consumption || 0,
            co2Saved: (data.energy_consumption || 0) * 0.5,
            speed: data.current_speed || 0,
            throttle: 0,
            brake: 0,
          },
          human: {
            lapTime: data.hamilton_lap_time || 0,
            energyConsumption: 0,
            co2Saved: 0,
            speed: 0,
            throttle: 0,
            brake: 0,
          }
        }));
        
        // Update simulation time and progress
        setSimulationTime(data.lap_time || 0);
        setCurrentLap(Math.floor((data.progress || 0) * 10)); // Convert progress to lap number
        
        // Update video frame if available
        if (data.video_frames && data.video_frames.length > 0) {
          const latestFrame = data.video_frames[data.video_frames.length - 1];
          try {
            const frameData = JSON.parse(latestFrame);
            if (frameData.frame_data) {
              setSimulationFrame(frameData.frame_data);
            }
          } catch (e) {
            console.log('Error parsing frame data:', e);
          }
        }
        
        // Also check for direct frame data
        if (data.current_frame) {
          setSimulationFrame(data.current_frame);
        }
        
        // Check if simulation completed
        if (data.status === 'completed') {
          setIsRunning(false);
          // Store results for comparison page
          if (data.comparison_data) {
            localStorage.setItem('simulationResults', JSON.stringify(data.comparison_data));
          }
        } else if (data.status === 'error') {
          setIsRunning(false);
          setSimulationError(data.error_message || 'Simulation error occurred');
        }
      } catch (error) {
        console.error('Error parsing stream data:', error);
      }
    };

    eventSourceRef.current.onerror = (error) => {
      console.error('EventSource error:', error);
      setSimulationError('Connection to simulation lost');
    };
  };

  const pauseSimulation = async () => {
    setIsRunning(false);
    // TODO: Implement pause functionality
  };

  const stopSimulation = async () => {
    try {
      console.log('🛑 Stopping simulation...');
      
      // Stop web simulation if available
      if (webSimulationAvailable) {
        try {
          const response = await fetch('http://localhost:5000/api/web-simulation/stop', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            }
          });
          
          if (response.ok) {
            console.log('✅ Web simulation stopped successfully');
          } else {
            console.error('❌ Failed to stop web simulation');
          }
        } catch (error) {
          console.error('❌ Error stopping web simulation:', error);
        }
      }
      
      // Close EventSource connection
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
        eventSourceRef.current = null;
        console.log('✅ EventSource connection closed');
      }
      
      // Update UI state
      setIsRunning(false);
      setSimulationError(null);
      console.log('✅ Simulation stopped');
      
    } catch (error) {
      console.error('💥 Error stopping simulation:', error);
      setIsRunning(false);
    }
  };

  const resetSimulation = async () => {
    try {
      console.log('🔄 Resetting simulation...');
      
      // Stop current simulation first
      await stopSimulation();
      
      // Clear all simulation data
      setIsRunning(false);
      setSimulationTime(0);
      setCurrentLap(0);
      setSimulationId(null);
      setSimulationFrame(null);
      setSimulationError(null);
      setFallbackMode(false);
      
      // Reset simulation data
      setSimulationData({
        ai: {
          lapTime: 0,
          energyConsumption: 0,
          co2Saved: 0,
          speed: 0,
          throttle: 0,
          brake: 0,
        },
        human: {
          lapTime: 0,
          energyConsumption: 0,
          co2Saved: 0,
          speed: 0,
          throttle: 0,
          brake: 0,
        }
      });
      
      // Clear localStorage
      localStorage.removeItem('simulationId');
      localStorage.removeItem('simulationResults');
      localStorage.removeItem('simulationConfig');
      
      console.log('✅ Simulation reset complete');
      
    } catch (error) {
      console.error('💥 Error resetting simulation:', error);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(1);
    return `${mins}:${secs.padStart(4, '0')}`;
  };

  // Poll for simulation updates when running
  useEffect(() => {
    let interval;
    if (isRunning && webSimulationAvailable) {
      interval = setInterval(async () => {
        try {
          const response = await fetch('http://localhost:5000/api/web-simulation/status');
          if (response.ok) {
            const data = await response.json();
            
            // Update simulation data
            setSimulationData(prev => ({
              ...prev,
              ai: {
                lapTime: data.lap_time || 0,
                energyConsumption: data.energy_consumption || 0,
                co2Saved: data.co2_saved || 0, // Use actual CO₂ savings from backend
                speed: data.current_speed || 0, // This now shows F1-scaled speeds
                throttle: 0,
                brake: 0,
              },
              human: {
                lapTime: data.hamilton_lap_time || 0,
                energyConsumption: 0,
                co2Saved: 0,
                speed: 0,
                throttle: 0,
                brake: 0,
              }
            }));
            
            // Update simulation time
            setSimulationTime(data.lap_time || 0);
            
            // Update video frame - simple and smooth
            if (data.video_frames && data.video_frames.length > 0) {
              const latestFrame = data.video_frames[data.video_frames.length - 1];
              try {
                const frameData = JSON.parse(latestFrame);
                if (frameData.frame_data) {
                  setSimulationFrame(frameData.frame_data);
                }
              } catch (e) {
                console.log('Error parsing frame data:', e);
              }
            }
            
            // Also check for direct frame data
            if (data.current_frame) {
              setSimulationFrame(data.current_frame);
            }
            
            // Check if simulation completed
            if (data.status === 'completed' || data.status === 'error') {
              setIsRunning(false);
              if (data.status === 'error') {
                setSimulationError(data.error || 'Simulation error occurred');
              }
            }
          }
        } catch (error) {
          console.error('Failed to fetch simulation status:', error);
        }
      }, 100); // Poll every 500ms for much smoother updates
    }
    
    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [isRunning, webSimulationAvailable]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
    };
  }, []);

  return (
    <div className="simulation-dashboard">
      <div className="dashboard-container">
        {/* Header */}
        <div className="dashboard-header">
          <div className="header-left">
            <Link to="/simulator" className="back-button">
              <ArrowLeft size={20} />
              Back to Setup
            </Link>
            <div className="simulation-info">
              <h1>Live Simulation</h1>
              <div className="simulation-meta">
                <span><MapPin size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').track || 'Unknown Track'}</span>
                <span><Car size={16} /> {JSON.parse(localStorage.getItem('simulationConfig') || '{}').model_version || 'PPO'} Model</span>
                <span><Clock size={16} /> {formatTime(simulationTime)}</span>
                {webSimulationAvailable && (
                  <span className="web-simulation-badge">Web Simulation</span>
                )}
                {fallbackMode && (
                  <span className="fallback-badge">Mock Simulation</span>
                )}
                {simulationError && (
                  <span className="error-badge">Error: {simulationError}</span>
                )}
                {!isRunning && !simulationError && (
                  <span className="status-badge">Ready to Start</span>
                )}
                {isRunning && (
                  <span className="status-badge running">Running...</span>
                )}
              </div>
            </div>
          </div>
          
          <div className="header-controls">
            <button 
              className={`control-btn ${isRunning ? 'pause' : 'play'}`}
              onClick={isRunning ? pauseSimulation : startSimulation}
            >
              {isRunning ? <Pause size={20} /> : <Play size={20} />}
              {isRunning ? 'Pause' : 'Start'}
            </button>
            {isRunning && (
              <button className="control-btn stop" onClick={stopSimulation}>
                <Square size={20} />
                Stop
              </button>
            )}
            <button className="control-btn reset" onClick={resetSimulation}>
              <RotateCcw size={20} />
              Reset
            </button>
          </div>
        </div>

        {/* Error Display */}
        {simulationError && (
          <div className="error-alert">
            <AlertCircle size={20} />
            <span>{simulationError}</span>
          </div>
        )}

        {/* Progress Bar */}
        <div className="progress-section">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${(currentLap / totalLaps) * 100}%` }}
            ></div>
          </div>
          <div className="progress-info">
            <span>Lap {currentLap} of {totalLaps}</span>
            <span>{formatTime(simulationTime)}</span>
          </div>
        </div>

        {/* Main Content */}
        <div className="dashboard-content">
          {/* Video Panel */}
          <div className="video-panel">
            <div className="video-container">
              <h3>Live Simulation</h3>
              <div className="video-frame">
                {simulationFrame ? (
                  <img 
                    src={simulationFrame} 
                    alt="Simulation" 
                    className="simulation-image"
                  />
                ) : (
                  <div className="video-placeholder">
                    <Car size={48} />
                    <p>{isRunning ? 'Simulation Running...' : 'No simulation active'}</p>
                    {fallbackMode && (
                      <small>Running in mock simulation mode</small>
                    )}
                    {!webSimulationAvailable && !fallbackMode && (
                      <small>Web simulation not available - using mock data</small>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Metrics Panel */}
          <div className="metrics-panel">
            <div className="metric-card">
              <div className="metric-header">
                <Zap size={20} />
                <span>AI Performance</span>
              </div>
              <div className="metric-value">{formatTime(simulationData.ai.lapTime)}</div>
              <div className="metric-label">Lap Time</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Gauge size={20} />
                <span>Speed</span>
              </div>
              <div className="metric-value">{simulationData.ai.speed.toFixed(1)}</div>
              <div className="metric-label">km/h</div>
            </div>

            <div className="metric-card">
              <div className="metric-header">
                <Leaf size={20} />
                <span>Energy Saved</span>
              </div>
              <div className="metric-value">{simulationData.ai.co2Saved.toFixed(1)}</div>
              <div className="metric-label">CO₂ Saved</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SimulationDashboard;
